#include <iostream>
using namespace std;

int main()
{
    int x, y, z;
    cin >> x >> y >> z;
    if (x % y == 0 && y % z == 0){
        x += 1;
        y += 1;
        z += 1;
    }
    else {
        x = 0;
        y = 0;
        z = 0;
    }
    cout << x << endl;
    cout << y << endl;
    cout << z << endl;
}
