#include <stdio.h>
#include <math.h>

float fx(float x) {
    float result;
    if (x < 1.22) {
        result = x * cos(x);
    } else if (x >= 1.22) {
        result = 5 * pow(x, 3) + 7;
    }
    return result;
}

int main() {
    printf("%f", fx(2.0));
    return 0;
}
