include <iostream>
using namespace std;

int main(){
    int numOfElements; //массивтың ұзындығы
    int x[numOfElements] //массив
    int counter = 0; //жай ғана санағыш
    for(int i=0; i<numOfElements; i++){
        if(x[i] % 2 ==1)
            counter++;
    }
    cout << counter*100/numOfElements;   
}
